import{_ as l}from"./plugin-vue_export-helper.21dcd24c.js";import{o as i,c as e,e as c}from"./app.0070c4d9.js";var p="/assets/TCP.259f4b40.png",d="/assets/tcp3.9312d297.png",o="/assets/tcp4.863fa59e.png";const a={},t=c('<h1 id="tcp" tabindex="-1"><a class="header-anchor" href="#tcp" aria-hidden="true">#</a> TCP</h1><h2 id="_1-\u4EC0\u4E48\u662Ftcp" tabindex="-1"><a class="header-anchor" href="#_1-\u4EC0\u4E48\u662Ftcp" aria-hidden="true">#</a> 1.\u4EC0\u4E48\u662FTCP</h2><p>TCP \u662F\u2FAF\u5411\u8FDE\u63A5\u7684\u3001\u53EF\u9760\u7684\u3001\u57FA\u4E8E\u5B57\u8282\u6D41\u7684<code>\u4F20\u8F93\u5C42</code>\u901A\u4FE1\u534F\u8BAE</p><ul><li>\u9762\u5411\u8FDE\u63A5\uFF1A\u4E00\u5BF9\u4E00\u3002\u4E0D\u80FD\u50CFUDP\u53EF\u4EE5\u2F00\u4E2A\u4E3B\u673A\u540C\u65F6\u5411\u591A\u4E2A\u4E3B\u673A\u53D1\u9001\u6D88\u606F</li><li>\u53EF\u9760\u7684\uFF1A\u2F46\u8BBA\u7684\u2F79\u7EDC\u94FE\u8DEF\u4E2D\u51FA\u73B0\u4E86\u600E\u6837\u7684\u94FE\u8DEF\u53D8\u5316\uFF0CTCP \u90FD\u53EF\u4EE5\u4FDD\u8BC1\u2F00\u4E2A\u62A5\u2F42\u2F00\u5B9A\u80FD\u591F\u5230\u8FBE\u63A5\u6536\u7AEF</li><li>\u5B57\u8282\u6D41\uFF1A\u6D88\u606F\u662F\u300C\u6CA1\u6709\u8FB9\u754C\u300D\u7684\uFF0C\u2F46\u8BBA\u6D88\u606F\u6709\u591A\u2F24\u90FD\u53EF\u4EE5\u8FDB\u2F8F\u4F20\u8F93\uFF0C\u5E76\u4E14\u6D88\u606F\u662F\u300C\u6709\u5E8F\u7684\u300D\uFF0C\u5F53\u300C\u524D\u2F00\u4E2A\u300D\u6D88\u606F\u6CA1\u6709\u6536\u5230\u7684\u65F6\u5019\uFF0C\u5373\u4F7F\u5B83\u5148\u6536\u5230\u4E86\u540E\u2FAF\u7684\u5B57\u8282\uFF0C\u4E5F\u4E0D\u80FD\u6254\u7ED9\u5E94\u2F64\u5C42\u53BB\u5904\u7406\uFF0C</li></ul><h2 id="_2-\u5982\u4F55\u552F\u4E00\u786E\u5B9A\u4E00\u4E2A-tcp-\u8FDE\u63A5" tabindex="-1"><a class="header-anchor" href="#_2-\u5982\u4F55\u552F\u4E00\u786E\u5B9A\u4E00\u4E2A-tcp-\u8FDE\u63A5" aria-hidden="true">#</a> 2.\u5982\u4F55\u552F\u2F00\u786E\u5B9A\u2F00\u4E2A TCP \u8FDE\u63A5</h2><p>TCP \u56DB\u5143\u7EC4\u53EF\u4EE5\u552F\u2F00\u7684\u786E\u5B9A\u2F00\u4E2A\u8FDE\u63A5\uFF0C\u56DB\u5143\u7EC4\u5305\u62EC\u5982\u4E0B\uFF1A</p><ul><li>\u6E90\u5730\u5740</li><li>\u6E90\u7AEF\u2F1D</li><li>\u2F6C\u7684\u5730\u5740</li><li>\u2F6C\u7684\u7AEF\u2F1D</li></ul><p>\u6709\u2F00\u4E2A IP \u7684\u670D\u52A1\u5668\u76D1\u542C\u4E86\u2F00\u4E2A\u7AEF\u2F1D\uFF0C\u5B83\u7684 TCP \u7684\u6700\u2F24\u8FDE\u63A5\u6570\u662F\u591A\u5C11\uFF1F</p><p>\u670D\u52A1\u5668\u901A\u5E38\u56FA\u5B9A\u5728\u67D0\u4E2A\u672C\u5730\u7AEF\u2F1D\u4E0A\u76D1\u542C\uFF0C\u7B49\u5F85\u5BA2\u6237\u7AEF\u7684\u8FDE\u63A5\u8BF7\u6C42\u3002 \u56E0\u6B64\uFF0C\u5BA2\u6237\u7AEF IP \u548C \u7AEF\u2F1D\u662F\u53EF\u53D8\u7684</p><blockquote><p>\u6700\u5927TCP\u8FDE\u63A5\u6570=\u5BA2\u6237\u7AEFIP\u6570\xD7\u5BA2\u6237\u7AEF\u7AEF\u53E3\u6570</p></blockquote><p>\u5BF9\u4E8EIPv4,\u5BA2\u6237\u7AEF\u7684 IP \u6570\u6700\u591A\u4E3A 2<sup>32</sup>\uFF0C\u5BA2\u6237\u7AEF\u7684\u7AEF\u2F1D\u6570\u6700\u591A\u4E3A 2<sup>16</sup>\uFF0C\u4E5F\u5C31\u662F\u670D\u52A1\u7AEF\u5355\u673A\u6700\u2F24TCP \u8FDE\u63A5\u6570\uFF0C\u7EA6\u4E3A 2<sup>48</sup>\u3002</p><h2 id="_3-tcp\u548Cudp" tabindex="-1"><a class="header-anchor" href="#_3-tcp\u548Cudp" aria-hidden="true">#</a> 3.TCP\u548CUDP</h2><ol><li><p>\u8FDE\u63A5</p><ul><li>TCP \u662F\u2FAF\u5411\u8FDE\u63A5\u7684\u4F20\u8F93\u5C42\u534F\u8BAE\uFF0C\u4F20\u8F93\u6570\u636E\u524D\u5148\u8981\u5EFA\u2F74\u8FDE\u63A5\u3002\u7C7B\u4F3C\u6253\u7535\u8BDD\uFF0C\u9700\u8981\u5BF9\u65B9\u63A5\u901A\u3002</li><li>UDP \u662F\u4E0D\u9700\u8981\u8FDE\u63A5\uFF0C\u5373\u523B\u4F20\u8F93\u6570\u636E\u3002\u7C7B\u4F3C\u4E8E\u5199\u4FE1\uFF0C\u5199\u5C31\u884C\u4E86</li></ul></li><li><p>\u670D\u52A1\u5BF9\u8C61</p><ul><li>TCP\u662F\u4E00\u5BF9\u4E00\u4E24\u70B9\u670D\u52A1,\u9700\u8981\u8FDE\u63A5</li><li>UDP\u652F\u6301\u4E00\u5BF9\u4E00\uFF0C\u4E00\u5BF9\u591A\uFF0C\u591A\u5BF9\u591A\u4EA4\u4E92\u901A\u4FE1</li></ul></li><li><p>\u53EF\u9760\u6027</p><ul><li>TCP \u662F\u53EF\u9760\u4EA4\u4ED8\u6570\u636E\u7684\uFF0C\u6570\u636E\u53EF\u4EE5\u2F46\u5DEE\u9519\u3001\u4E0D\u4E22\u5931\u3001\u4E0D\u91CD\u590D\u3001\u6309\u9700\u5230\u8FBE\u3002</li><li>UDP \u662F\u5C3D\u6700\u2F24\u52AA\u2F12\u4EA4\u4ED8\uFF0C\u4E0D\u4FDD\u8BC1\u53EF\u9760\u4EA4\u4ED8\u6570\u636E\u3002</li></ul></li><li><p>\u62E5\u585E\u63A7\u5236\u3001\u6D41\u91CF\u63A7\u5236</p><ul><li>TCP \u6709\u62E5\u585E\u63A7\u5236\u548C\u6D41\u91CF\u63A7\u5236\u673A\u5236\uFF0C\u4FDD\u8BC1\u6570\u636E\u4F20\u8F93\u7684\u5B89\u5168\u6027\u3002</li><li>UDP \u6CA1\u6709\uFF0C\u5373\u4F7F\u2F79\u7EDC\u2FAE\u5E38\u62E5\u5835\u4E86\uFF0C\u4E5F\u4E0D\u4F1A\u5F71\u54CD UDP \u7684\u53D1\u9001\u901F\u7387\u3002</li></ul></li><li><p>\u2FB8\u90E8\u5F00\u9500</p><ul><li>TCP \u2FB8\u90E8\u2ED3\u5EA6\u8F83\u2ED3\uFF0C\u2FB8\u90E8\u5728\u6CA1\u6709\u4F7F\u2F64\u300C\u9009\u9879\u300D\u5B57\u6BB5\u65F6\u662F 20 \u4E2A\u5B57\u8282\uFF0C\u5982\u679C\u4F7F\u2F64\u4E86\u300C\u9009\u9879\u300D\u5B57\u6BB5\u5219\u4F1A\u53D8\u2ED3\u7684\u3002</li><li>UDP \u2FB8\u90E8\u53EA\u6709 8 \u4E2A\u5B57\u8282\uFF0C\u5E76\u4E14\u662F\u56FA\u5B9A\u4E0D\u53D8\u7684\uFF0C\u5F00\u9500\u8F83\u2F29\u3002</li></ul></li><li><p>\u4F20\u8F93\u2F45\u5F0F</p><ul><li>TCP \u662F\u6D41\u5F0F\u4F20\u8F93\uFF0C\u6CA1\u6709\u8FB9\u754C\uFF0C\u4F46\u4FDD\u8BC1\u987A\u5E8F\u548C\u53EF\u9760\u3002\u9762\u5411\u5B57\u8282\u6D41</li><li>UDP \u662F\u5BF9\u5E94\u7528\u5C42\u4EA4\u4ED8\u7684\u62A5\u6587\u76F4\u63A5\u6253\u5305\uFF0C\u2F00\u4E2A\u5305\u2F00\u4E2A\u5305\u7684\u53D1\u9001\uFF0C\u662F\u6709\u8FB9\u754C\u7684\uFF0C\u4F46\u53EF\u80FD\u4F1A\u4E22\u5305\u548C\u4E71\u5E8F</li></ul></li></ol><p>\u4E3A\u4EC0\u4E48UDP \u5934\u90E8\u6CA1\u6709\u300C\u2FB8\u90E8\u2ED3\u5EA6\u300D\u5B57\u6BB5\uFF0C\u2F7D TCP \u5934\u90E8\u6709\u300C\u2FB8\u90E8\u2ED3\u5EA6\u300D\u5B57\u6BB5</p><ul><li>\u539F\u56E0\u662F TCP \u6709\u53EF\u53D8\u2ED3\u7684\u300C\u9009\u9879\u300D\u5B57\u6BB5\uFF0C\u2F7D UDP \u5934\u90E8\u2ED3\u5EA6\u5219\u662F\u4E0D\u4F1A\u53D8\u5316\u7684\uFF0C\u2F46\u9700\u591A\u2F00\u4E2A\u5B57\u6BB5\u53BB\u8BB0\u5F55 UDP \u7684\u2FB8\u90E8\u2ED3\u5EA6</li></ul><h2 id="_4-tcp\u548Cudp\u5E94\u7528\u573A\u666F" tabindex="-1"><a class="header-anchor" href="#_4-tcp\u548Cudp\u5E94\u7528\u573A\u666F" aria-hidden="true">#</a> 4.TCP\u548CUDP\u5E94\u7528\u573A\u666F</h2><ol><li><p>TCP\u9762\u5411\u8FDE\u63A5\uFF0C\u80FD\u4FDD\u8BC1\u6570\u636E\u7684\u53EF\u9760\u6027\u4EA4\u4ED8\uFF0C\u5E38\u7528\u4E8E</p><ul><li>FTP \u6587\u4EF6\u4F20\u8F93</li><li>HTTP/HTTPS</li></ul></li><li><p>UDP\u65E0\u8FDE\u63A5\uFF0C\u53EF\u4EE5\u968F\u65F6\u53D1\u9001\u6570\u636E\uFF0C\u5904\u7406\u6570\u636E\u7B80\u5355\u9AD8\u6548</p><ul><li>\u89C6\u9891\uFF0C\u97F3\u9891\u7B49\u591A\u5A92\u4F53\u901A\u4FE1</li><li>\u5E7F\u64AD\u901A\u4FE1</li><li>\u57DF\u540D\u67E5\u8BE2</li><li>\u96A7\u9053\u7F51\u7EDC\uFF0C\u6BD4\u5982\uFF1Avpn,VXLAN.</li></ul></li></ol><h2 id="_5-tcp\u8FDE\u63A5\u5EFA\u7ACB" tabindex="-1"><a class="header-anchor" href="#_5-tcp\u8FDE\u63A5\u5EFA\u7ACB" aria-hidden="true">#</a> 5.TCP\u8FDE\u63A5\u5EFA\u7ACB</h2><p><img src="'+p+'" alt="TCP"></p><h3 id="_5-1-tcp-\u4E09\u6B21\u63E1\u624B" tabindex="-1"><a class="header-anchor" href="#_5-1-tcp-\u4E09\u6B21\u63E1\u624B" aria-hidden="true">#</a> 5.1 TCP \u4E09\u6B21\u63E1\u2F3F</h3><p>TCP\u901A\u8FC7\u4E09\u6B21\u63E1\u624B\u5EFA\u7ACB\u8FDE\u63A5 ,\u521A\u5F00\u59CB\u5BA2\u6237\u7AEF\u5904\u4E8E Closed \u7684\u72B6\u6001\uFF0C\u670D\u52A1\u7AEF\u5904\u4E8E Listen \u72B6\u6001\uFF0C\u4E3B\u52A8\u76D1\u542C\u67D0\u4E2A\u7AEF\u53E3</p><ol><li><p>\u5BA2\u6237\u7AEF\u968F\u673A\u521D\u59CB\u5316\u5E8F\u5217\u53F7\uFF08<code>client_isn</code>)\u586B\u5165TCP\u9996\u90E8,\u628A<code>SYN</code>\u7F6E\u4E3A1\uFF0C\u8868\u793ASYN\u62A5\u6587\uFF0C\u63A5\u7740\u628A\u7B2C\u4E00\u4E2ASYN\u62A5\u6587\u53D1\u7ED9\u670D\u52A1\u7AEF\uFF0C\u8868\u793A\u53D1\u8D77\u8FDE\u63A5\uFF0C\u8BE5\u62A5\u6587\u4E0D\u5305\u542B\u5E94\u7528\u5C42\u6570\u636E\uFF0C\u4E4B\u540E\u5BA2\u6237\u7AEF\u5904\u4E8E<code>SYN-SENT</code>\u72B6\u6001</p></li><li><p>\u670D\u52A1\u7AEF\u6536\u5230\u540E\uFF0C\u9996\u5148\u4E5F\u4F1A\u968F\u673A\u521D\u59CB\u5316\u81EA\u5DF1\u7684\u5E8F\u5217\u53F7\uFF08<code>server_isn</code>) ,\u586B\u5165TCP\u9996\u90E8,\u628ATCP\u9996\u90E8<code>\u786E\u8BA4\u5E94\u7B54\u53F7</code>\u586B\u5165<code>client_isn+1</code>,\u518D\u628A <code>SYN </code>\u548C<code>ACK</code> \u6807\u5FD7\u4F4D\u7F6E\u4E3A 1\uFF0C\u6700\u540E\u628A\u8BE5\u62A5\u2F42\u53D1\u7ED9\u5BA2\u6237\u7AEF\uFF0C\u8BE5\u62A5\u2F42\u4E5F\u4E0D\u5305\u542B\u5E94\u2F64\u5C42\u6570\u636E\uFF0C\u4E4B\u540E\u670D\u52A1\u7AEF\u5904\u4E8E <code>SYN-RCVD</code>\u72B6\u6001\u3002</p></li><li><p>\u5BA2\u6237\u7AEF\u6536\u5230\u540E\uFF0C\u8FD8\u8981\u53D1\u9001\u4E00\u4E2A\u5E94\u7B54\u62A5\u6587\uFF0C\u2FB8\u5148\u8BE5\u5E94\u7B54\u62A5\u2F42 TCP \u2FB8\u90E8 <code>ACK </code>\u6807\u5FD7\u4F4D\u7F6E\u4E3A1\uFF0C\u5176\u6B21 <code>\u786E\u8BA4\u5E94\u7B54\u53F7</code> \u5B57\u6BB5\u586B\u2F0A<code> server_isn + 1</code>,\u6700\u540E\u628A\u62A5\u2F42\u53D1\u9001\u7ED9\u670D\u52A1\u7AEF\uFF0C\u8FD9\u6B21\u62A5\u2F42\u53EF\u4EE5\u643A\u5E26\u5BA2\u6237\u5230\u670D\u52A1\u5668\u7684\u6570\u636E\uFF0C\u4E4B\u540E\u5BA2\u6237\u7AEF\u5904\u4E8E <code>ESTABLISHED </code>\u72B6\u6001\u3002</p></li><li><p>\u670D\u52A1\u5668\u6536\u5230\u5BA2\u6237\u7AEF\u7684\u5E94\u7B54\u62A5\u2F42\u540E\uFF0C\u4E5F\u8FDB\u2F0A <code>ESTABLISHED</code> \u72B6\u6001</p></li></ol><blockquote><p>\u7B2C\u4E09\u6B21\u63E1\u2F3F\u662F\u53EF\u4EE5\u643A\u5E26\u6570\u636E\uFF0C\u524D\u4E24\u6B21\u63E1\u2F3F\u662F\u4E0D\u53EF\u4EE5\u643A\u5E26\u6570\u636E</p></blockquote><p><img src="'+d+'" alt="3"></p><p>TCP \u7684\u8FDE\u63A5\u72B6\u6001\u67E5\u770B\uFF0C\u5728 Linux \u53EF\u4EE5\u901A\u8FC7 netstat -napt \u547D\u4EE4\u67E5\u770B\u3002</p><p>\u4E3A\u4EC0\u4E48\u662F\u4E09\u6B21\uFF1F\u4E0D\u662F\u56DB\u6B21\uFF0C\u4E24\u6B21\uFF1F</p><ol><li><p>\u4E09\u6B21\u63E1\u2F3F\u624D\u53EF\u4EE5\u963B\u2F4C\u91CD\u590D\u5386\u53F2\u8FDE\u63A5\u7684\u521D\u59CB\u5316\uFF08\u4E3B\u8981\u539F\u56E0\uFF09</p><ul><li>\u5F53\u7B2C\u4E8C\u6B21\u63E1\u624B\u5BA2\u6237\u7AEF\u6536\u5230SYN+ACK\u62A5\u6587\u540E\uFF0C\u6839\u636E\u81EA\u8EAB\u4E0A\u4E0B\u6587\u5224\u65AD\u662F\u4E00\u4E2A\u5386\u53F2\u8FDE\u63A5\uFF08\u5E8F\u5217\u53F7\u8FC7\u671F\u6216\u8005\u8D85\u65F6\uFF09\uFF0C\u5BA2\u6237\u7AEF\u4F1A\u901A\u8FC7\u7B2C\u4E09\u6B21\u63E1\u624B\u53D1\u9001RST\u62A5\u6587\u7ED9\u670D\u52A1\u7AEF\u7EC8\u6B62\u6B64\u6B21\u8FDE\u63A5\uFF0C\u663E\u7136\u4E24\u6B21\u63E1\u624B\u4E0D\u80FD\u5224\u65AD\u5F53\u524D\u8FDE\u63A5\u662F\u5426\u662F\u5386\u53F2\u8FDE\u63A5</li></ul></li><li><p>\u4E09\u6B21\u63E1\u2F3F\u624D\u53EF\u4EE5\u540C\u6B65\u53CC\u2F45\u7684\u521D\u59CB\u5E8F\u5217\u53F7</p><ul><li>\u4E24\u6B21\u63E1\u624B\u53EA\u4FDD\u8BC1\u4E86\u4E00\u65B9\u7684\u5E8F\u5217\u53F7\u80FD\u88AB\u5BF9\u65B9\u63A5\u6536\uFF0C\u6CA1\u529E\u6CD5\u4FDD\u8BC1\u53CC\u2F45\u7684\u521D\u59CB\u5E8F\u5217\u53F7\u90FD\u80FD\u88AB\u786E\u8BA4\u63A5\u6536\u3002</li><li>\u56DB\u6B21\u63E1\u2F3F\u5176\u5B9E\u4E5F\u80FD\u591F\u53EF\u9760\u7684\u540C\u6B65\u53CC\u2F45\u7684\u521D\u59CB\u5316\u5E8F\u53F7\uFF0C\u4F46\u7B2C\u2F06\u6B65\u548C\u7B2C\u4E09\u6B65\u53EF\u4EE5\u4F18\u5316\u6210\u2F00\u6B65</li></ul></li><li><p>\u4E09\u6B21\u63E1\u2F3F\u624D\u53EF\u4EE5\u907F\u514D\u8D44\u6E90\u6D6A\u8D39</p><ul><li>\u4E24\u6B21\u63E1\u624B\u3002\u5BA2\u6237\u7AEFSYN\u963B\u585E\u65F6\uFF0C\u6CA1\u6709\u63A5\u53D7\u5230ACK\u4F1A\u91CD\u53D1\uFF0C\u6B64\u65F6\u670D\u52A1\u5668\u54CD\u5E94SYN+ACK\u5EFA\u7ACB\u8FDE\u63A5\uFF0C</li><li>\u4E00\u6BB5\u65F6\u95F4\u540E\u88AB\u963B\u585E\u7684SYN\u62A5\u6587\u5230\u8FBE\uFF0C\u6B64\u65F6\u670D\u52A1\u5668\u4E0D\u6E05\u695A\u5BA2\u6237\u7AEF\u662F\u5426\u6536\u5230ACK\u786E\u8BA4\u4FE1\u53F7\uFF0C\u6240\u4EE5\u53EA\u80FD\u5EFA\u7ACB\u8FDE\u63A5</li><li>\u6240\u4EE5\u5982\u679C\u5BA2\u6237\u7AEF\u7684 SYN \u963B\u585E\u4E86\uFF0C\u91CD\u590D\u53D1\u9001\u591A\u6B21 SYN \u62A5\u2F42\uFF0C\u90A3\u4E48\u670D\u52A1\u5668\u5728\u6536\u5230\u8BF7\u6C42\u540E\u5C31\u4F1A\u5EFA\u2F74\u591A\u4E2A\u5197\u4F59\u7684\u2F46\u6548\u94FE\u63A5\uFF0C\u9020\u6210\u4E0D\u5FC5\u8981\u7684\u8D44\u6E90\u6D6A\u8D39\u3002</li></ul></li></ol><p>\u4E3A\u4EC0\u4E48\u5BA2\u6237\u7AEF\u548C\u670D\u52A1\u7AEF\u7684\u521D\u59CB\u5E8F\u5217\u53F7 ISN \u662F\u4E0D\u76F8\u540C\uFF1A</p><ul><li>\u5982\u679C\u5E8F\u5217\u53F7\u76F8\u540C\uFF0C\u2F46\u6CD5\u5206\u8FA8\u51FA\u8BE5\u62A5\u2F42\u662F\u4E0D\u662F\u5386\u53F2\u62A5\u2F42\uFF0C\u5982\u679C\u5386\u53F2\u62A5\u2F42\u88AB\u65B0\u7684\u8FDE\u63A5\u63A5\u6536\u4E86\uFF0C\u5219\u4F1A\u4EA7\u2F63\u6570\u636E\u9519\u4E71\u3002</li><li>\u4E3A\u4E86\u5B89\u5168\u6027\uFF0C\u9632\u2F4C\u2FCA\u5BA2\u4F2A\u9020\u7684\u76F8\u540C\u5E8F\u5217\u53F7\u7684 TCP \u62A5\u2F42\u88AB\u5BF9\u2F45\u63A5\u6536</li></ul><h2 id="_6-tcp\u8FDE\u63A5\u65AD\u5F00" tabindex="-1"><a class="header-anchor" href="#_6-tcp\u8FDE\u63A5\u65AD\u5F00" aria-hidden="true">#</a> 6.TCP\u8FDE\u63A5\u65AD\u5F00</h2><h3 id="_6-1-tcp\u56DB\u6B21\u6325\u624B" tabindex="-1"><a class="header-anchor" href="#_6-1-tcp\u56DB\u6B21\u6325\u624B" aria-hidden="true">#</a> 6.1 TCP\u56DB\u6B21\u6325\u624B</h3><p>\u53CC\u2F45\u90FD\u53EF\u4EE5\u4E3B\u52A8\u65AD\u5F00\u8FDE\u63A5\uFF0C\u65AD\u5F00\u8FDE\u63A5\u540E\u4E3B\u673A\u4E2D\u7684\u300C\u8D44\u6E90\u300D\u5C06\u88AB\u91CA\u653E\u3002</p><p><img src="'+o+'" alt="4"></p><ol><li><p>\u5BA2\u6237\u7AEF\u6253\u7B97\u5173\u95ED\u8FDE\u63A5\uFF0C\u4F1A\u53D1\u9001\u2F00\u4E2A TCP \u2FB8\u90E8 <code>FIN </code>\u6807\u5FD7\u4F4D\u88AB\u7F6E\u4E3A 1 \u7684\u62A5\u2F42\uFF0C\u4E5F\u5373 <code>FIN</code> \u62A5\u2F42\uFF0C\u4E4B\u540E\u5BA2\u6237\u7AEF\u8FDB\u2F0A <code>FIN_WAIT_1 </code>\u72B6\u6001\u3002</p></li><li><p>\u670D\u52A1\u7AEF\u6536\u5230\u8BE5\u62A5\u2F42\u540E\uFF0C\u5C31\u5411\u5BA2\u6237\u7AEF\u53D1\u9001 <code>ACK</code> \u5E94\u7B54\u62A5\u2F42\uFF0C\u63A5\u7740\u670D\u52A1\u7AEF\u8FDB\u2F0A <code>CLOSED_WAIT </code>\u72B6\u6001</p></li><li><p>\u5BA2\u6237\u7AEF\u6536\u5230\u670D\u52A1\u7AEF\u7684 <code>ACK </code>\u5E94\u7B54\u62A5\u2F42\u540E\uFF0C\u4E4B\u540E\u8FDB\u2F0A<code> FIN_WAIT_2</code> \u72B6\u6001\u3002</p></li><li><p>\u7B49\u5F85\u670D\u52A1\u7AEF\u5904\u7406\u5B8C\u6570\u636E\u540E\uFF0C\u4E5F\u5411\u5BA2\u6237\u7AEF\u53D1\u9001 <code>FIN </code>\u62A5\u2F42\uFF0C\u4E4B\u540E\u670D\u52A1\u7AEF\u8FDB\u2F0A<code> LAST_ACK</code> \u72B6\u6001\u3002</p></li><li><p>\u5BA2\u6237\u7AEF\u6536\u5230\u670D\u52A1\u7AEF\u7684 <code>FIN</code> \u62A5\u2F42\u540E\uFF0C\u56DE\u2F00\u4E2A <code>ACK</code> \u5E94\u7B54\u62A5\u2F42\uFF0C\u4E4B\u540E\u8FDB\u2F0A <code>TIME_WAIT </code>\u72B6\u6001</p></li><li><p>\u670D\u52A1\u5668\u6536\u5230\u4E86<code>ACK</code>\u5E94\u7B54\u62A5\u2F42\u540E\uFF0C\u5C31\u8FDB\u2F0A\u4E86<code> CLOSED</code> \u72B6\u6001\uFF0C\u2F84\u6B64\u670D\u52A1\u7AEF\u5DF2\u7ECF\u5B8C\u6210\u8FDE\u63A5\u7684\u5173\u95ED\u3002</p></li><li><p>\u5BA2\u6237\u7AEF\u5728\u7ECF\u8FC7 <code>2MSL </code>(\u62A5\u6587\u6700\u5927\u751F\u5B58\u65F6\u95F4\uFF09\u2F00\u6BB5\u65F6\u95F4\u540E\uFF0C\u2F83\u52A8\u8FDB\u2F0A <code>CLOSED </code>\u72B6\u6001\uFF0C\u2F84\u6B64\u5BA2\u6237\u7AEF\u4E5F\u5B8C\u6210\u8FDE\u63A5\u7684\u5173\u95ED</p></li></ol><blockquote><p>\u6BCF\u4E2A\u2F45\u5411\u90FD\u9700\u8981\u2F00\u4E2A FIN \u548C\u2F00\u4E2A ACK,\u4E3B\u52A8\u5173\u95ED\u8FDE\u63A5\u7684\uFF0C\u624D\u6709 TIME_WAIT \u72B6\u6001\u3002</p></blockquote><p>\u4E3A\u4EC0\u4E48\u6325\u2F3F\u9700\u8981\u56DB\u6B21:</p><ul><li>\u5173\u95ED\u8FDE\u63A5\u65F6\uFF0C\u5BA2\u6237\u7AEF\u5411\u670D\u52A1\u7AEF\u53D1\u9001 FIN \u65F6\uFF0C\u4EC5\u8868\u793A\u5BA2\u6237\u7AEF\u4E0D\u518D\u53D1\u9001\u6570\u636E\u4E86\u4F46\u662F\u8FD8\u80FD\u63A5\u6536\u6570\u636E\u3002</li><li>\u670D\u52A1\u5668\u6536\u5230\u5BA2\u6237\u7AEF\u7684 FIN \u62A5\u2F42\u65F6\uFF0C\u5148\u56DE\u2F00\u4E2A ACK \u5E94\u7B54\u62A5\u2F42\uFF0C\u2F7D\u670D\u52A1\u7AEF\u53EF\u80FD\u8FD8\u6709\u6570\u636E\u9700\u8981\u5904\u7406\u548C\u53D1\u9001,\u7B49\u670D\u52A1\u7AEF\u4E0D\u518D\u53D1\u9001\u6570\u636E\u65F6\uFF0C\u624D\u53D1\u9001 FIN \u62A5\u2F42\u7ED9\u5BA2\u6237\u7AEF\u6765\u8868\u793A\u540C\u610F\u73B0\u5728\u5173\u95ED\u8FDE\u63A5</li><li>\u670D\u52A1\u7AEF\u901A\u5E38\u9700\u8981\u7B49\u5F85\u5B8C\u6210\u6570\u636E\u7684\u53D1\u9001\u548C\u5904\u7406\uFF0C\u6240\u4EE5\u670D\u52A1\u7AEF\u7684 ACK \u548C FIN \u2F00\u822C\u90FD\u4F1A\u5206\u5F00\u53D1\u9001\uFF0C\u4ECE\u2F7D\u2F50\u4E09\u6B21\u63E1\u2F3F\u5BFC\u81F4\u591A\u4E86\u2F00\u6B21</li></ul><p>\u4E3A\u4EC0\u4E48\u9700\u8981 TIME_WAIT \u72B6\u6001</p><ul><li>\u9632\u2F4C\u65E7\u8FDE\u63A5\u7684\u6570\u636E\u5305 <ul><li>\u6CA1\u6709TIME_WAIT,\u6709\u76F8\u540C\u7AEF\u2F1D\u7684 TCP \u8FDE\u63A5\u88AB\u590D\u2F64\u540E, \u5BA2\u6237\u7AEF\u53EF\u80FD\u4F1A\u63A5\u6536\u88AB\u5EF6\u8FDF\u7684\u6570\u636E\u5305 \uFF0C\u4EA7\u751F\u6570\u636E\u9519\u4E71</li></ul></li><li>\u4FDD\u8BC1\u8FDE\u63A5\u6B63\u786E\u5173\u95ED <ul><li>\u6700\u540E\u2F00\u4E2A ACK \u62A5\u2F42\u5982\u679C\u5728\u2F79\u7EDC\u4E2D\u88AB\u4E22\u5931\u4E86\uFF0C\u6B64\u65F6TIME-WAIT \u6CA1\u6709\u7B49\u5F85\u65F6\u95F4\u6216\u65F6\u95F4\u8FC7\u77ED\uFF0C\u76F4\u63A5\u8FDB\u5165CLOSED\u72B6\u6001\uFF0C\u90A3\u4E48\u670D\u52A1\u7AEF\u5219\u4F1A\u2F00\u76F4\u5904\u5728 LASE_ACK \u72B6\u6001\uFF0C\u963B\u6B62\u4E0B\u4E00\u6B21\u8FDE\u63A5</li></ul></li></ul><h2 id="_7-tcp\u91CD\u4F20\u673A\u5236" tabindex="-1"><a class="header-anchor" href="#_7-tcp\u91CD\u4F20\u673A\u5236" aria-hidden="true">#</a> 7.TCP\u91CD\u4F20\u673A\u5236</h2><ol><li>\u8D85\u65F6\u91CD\u4F20</li></ol><ul><li>\u5728\u53D1\u9001\u6570\u636E\u65F6\uFF0C\u8BBE\u5B9A\u2F00\u4E2A\u5B9A\u65F6\u5668\uFF0C\u5F53\u8D85\u8FC7\u6307\u5B9A\u7684\u65F6\u95F4\u540E\uFF0C\u6CA1\u6709\u6536\u5230\u5BF9\u2F45\u7684 ACK \u786E\u8BA4\u5E94\u7B54\u62A5\u2F42\uFF0C\u5C31\u4F1A\u91CD\u53D1\u8BE5\u6570\u636E\u3002</li><li>\u6570\u636E\u5305\u4E22\u5931\u6216\u786E\u8BA4\u5E94\u7B54\u4E22\u5931\u4F1A\u53D1\u751F\u8D85\u65F6\u91CD\u4F20</li></ul><ol start="2"><li>SACK \u2F45\u6CD5</li></ol>',43),r=[t];function u(h,s){return i(),e("div",null,r)}var P=l(a,[["render",u],["__file","TCP.html.vue"]]);export{P as default};
