import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";import{o as s,c as n,e as a}from"./app.946a5a33.js";var c="/assets/congestion1.cc70c756.jpg",t="/assets/congestion2.01b58713.jpg",r="/assets/congestion3.3ff9f60b.png";const d={},o=a('<h1 id="\u62E5\u585E\u63A7\u5236" tabindex="-1"><a class="header-anchor" href="#\u62E5\u585E\u63A7\u5236" aria-hidden="true">#</a> \u62E5\u585E\u63A7\u5236</h1><blockquote><p>\u5728\u7F51\u7EDC\u51FA\u73B0\u62E5\u5835\u65F6\uFF0C\u907F\u514D\u53D1\u9001\u65B9\u6570\u636E\u586B\u6EE1\u7F51\u7EDC</p></blockquote><h2 id="\u62E5\u585E\u7A97\u53E3" tabindex="-1"><a class="header-anchor" href="#\u62E5\u585E\u7A97\u53E3" aria-hidden="true">#</a> \u62E5\u585E\u7A97\u53E3</h2><blockquote><p>\u62E5\u585E\u7A97\u53E3\u662F\u53D1\u9001\u65B9\u7EF4\u62A4\u7684\u72B6\u6001\u53D8\u91CF\uFF0C\u6839\u636E\u7F51\u7EDC\u62E5\u585E\u7A0B\u5EA6\u52A8\u6001\u53D8\u5316</p></blockquote><p>\u53D1\u9001\u7A97\u53E3 swnd \u548C\u63A5\u6536\u7A97\u53E3 rwnd \u662F\u7EA6\u7B49\u4E8E\u7684\u5173\u7CFB\uFF0C\u7531\u4E8E\u52A0\u5165\u4E86\u62E5\u585E\u7A97\u53E3\u7684\u6982\u5FF5\u540E\uFF0C\u6B64\u65F6\u53D1\u9001\u7A97\u53E3\u7684\u503C\u662Fswnd = min(cwnd, rwnd)\uFF0C\u662F\u62E5\u585E\u7A97\u53E3\u548C\u63A5\u6536\u7A97\u53E3\u4E2D\u7684\u6700\u5C0F\u503C</p><p><b>\u53D1\u751F\u4E86\u8D85\u65F6\u91CD\u4F20\uFF0C\u5C31\u4F1A\u8BA4\u4E3A\u7F51\u7EDC\u51FA\u73B0\u4E86\u62E5\u585E\u3002</b></p><h2 id="\u62E5\u585E\u63A7\u5236\u7B97\u6CD5" tabindex="-1"><a class="header-anchor" href="#\u62E5\u585E\u63A7\u5236\u7B97\u6CD5" aria-hidden="true">#</a> \u62E5\u585E\u63A7\u5236\u7B97\u6CD5</h2><h3 id="\u6162\u542F\u52A8" tabindex="-1"><a class="header-anchor" href="#\u6162\u542F\u52A8" aria-hidden="true">#</a> \u6162\u542F\u52A8</h3><blockquote><p>\u53D1\u9001\u65B9\u6BCF\u6536\u5230\u4E00\u4E2AACK, cwnd + 1 ,\u4E00\u70B9\u4E00\u70B9\u7684\u63D0\u9AD8\u53D1\u9001\u6570\u636E\u5305\u7684\u6570\u91CF</p></blockquote><p>\u6709\u4E00\u4E2A\u53EB\u6162\u542F\u52A8\u95E8\u9650 <code>ssthresh \uFF08slow start threshold\uFF09</code>\u72B6\u6001\u53D8\u91CF\u3002</p><ul><li>\u5F53 cwnd &lt; ssthresh \u65F6\uFF0C\u4F7F\u7528\u6162\u542F\u52A8\u7B97\u6CD5\u3002</li><li>\u5F53 cwnd &gt;= ssthresh \u65F6\uFF0C\u5C31\u4F1A\u4F7F\u7528\u62E5\u585E\u907F\u514D\u7B97\u6CD5</li></ul><p>\u5047\u5B9Aswnd = cwnd</p><ul><li>\u8FDE\u63A5\u5EFA\u7ACB\u5B8C\u6210\u540E\uFF0C\u4E00\u5F00\u59CB\u521D\u59CB\u5316 cwnd = 1\uFF0C\u8868\u793A\u53EF\u4EE5\u4F20\u4E00\u4E2A MSS \u5927\u5C0F\u7684\u6570\u636E</li><li>\u6536\u5230\u4E00\u4E2AACK\u540E\uFF0Ccwnd + 1,\u4E00\u6B21\u80FD\u53D12\u4E2A</li><li>\u6536\u5230\u4E24\u4E2AACK,\u6BCF\u6536\u5230\u4E00\u4E2AACK\u52A0\u4E00\uFF0Ccwnd + 2\uFF0C\u4E8E\u662F\u4E00\u6B21\u80FD\u53D14\u4E2A</li><li>\u6536\u5230\u56DB\u4E2AACK\uFF0Ccwnd + 4,\u4E00\u6B21\u80FD\u53D18\u4E2A</li></ul><blockquote><p>\u6162\u542F\u52A8\u7B97\u6CD5\uFF0C\u53D1\u5305\u4E2A\u6570\u662F\u6307\u6570\u6027\u589E\u957F</p></blockquote><h3 id="\u62E5\u585E\u907F\u514D" tabindex="-1"><a class="header-anchor" href="#\u62E5\u585E\u907F\u514D" aria-hidden="true">#</a> \u62E5\u585E\u907F\u514D</h3><p>\u5F53\u62E5\u585E\u7A97\u53E3 cwnd \u8D85\u8FC7\u6162\u542F\u52A8\u95E8\u9650 ssthresh \u5C31\u4F1A\u8FDB\u5165\u62E5\u585E\u907F\u514D\u7B97\u6CD5\u3002</p><p>\u62E5\u585E\u907F\u514D\u7B97\u6CD5\u662F\u5C06\u539F\u672C\u6162\u542F\u52A8\u7B97\u6CD5\u7684\u6307\u6570\u589E\u957F\u53D8\u6210\u4E86\u7EBF\u6027\u589E\u957F\uFF0C\u8FD8\u662F\u589E\u957F\u9636\u6BB5\uFF0C\u4F46\u662F\u589E\u957F\u901F\u5EA6\u7F13\u6162\u4E86\u4E00\u4E9B</p><p>\u5B83\u7684\u89C4\u5219\u662F\uFF1A<b>\u6BCF\u5F53\u6536\u5230\u4E00\u4E2A ACK \u65F6\uFF0Ccwnd \u589E\u52A0 1/cwnd</b></p><p>\u63A5\u4E0A\u524D\u9762\u7684\u6162\u542F\u52A8\u7684\u6817\u5B50\uFF0C\u73B0\u5047\u5B9A ssthresh \u4E3A 8\uFF1A</p><p>\u5F53 8 \u4E2A ACK \u5E94\u7B54\u786E\u8BA4\u5230\u6765\u65F6\uFF0C\u6BCF\u4E2A\u786E\u8BA4\u589E\u52A0 1/8\uFF0C8 \u4E2A ACK \u786E\u8BA4 cwnd \u4E00\u5171\u589E\u52A0 1\uFF0C\u4E8E\u662F\u8FD9\u4E00\u6B21\u80FD\u591F\u53D1\u9001 9 \u4E2A MSS \u5927\u5C0F\u7684\u6570\u636E\uFF0C\u53D8\u6210\u4E86\u7EBF\u6027\u589E\u957F\u3002</p><p><img src="'+c+'" alt="congestion" loading="lazy"></p><h3 id="\u62E5\u585E\u53D1\u751F" tabindex="-1"><a class="header-anchor" href="#\u62E5\u585E\u53D1\u751F" aria-hidden="true">#</a> \u62E5\u585E\u53D1\u751F</h3><p>\u5F53\u7F51\u7EDC\u51FA\u73B0\u62E5\u585E\uFF0C\u4E5F\u5C31\u662F\u4F1A\u53D1\u751F\u6570\u636E\u5305\u91CD\u4F20\uFF0C\u91CD\u4F20\u673A\u5236\u4E3B\u8981\u6709\u4E24\u79CD\uFF1A</p><ul><li>\u8D85\u65F6\u91CD\u4F20</li><li>\u5FEB\u901F\u91CD\u4F20</li></ul><blockquote><p>\u53D1\u751F\u8D85\u65F6\u91CD\u4F20\u4F7F\u7528\u62E5\u585E\u53D1\u751F\u7B97\u6CD5:</p></blockquote><p>ssthresh \u548C cwnd \u7684\u503C\u4F1A\u53D1\u751F\u53D8\u5316\uFF1A</p><p>ssthresh \u8BBE\u4E3A cwnd/2\uFF0C<br> cwnd \u91CD\u7F6E\u4E3A 1 \uFF08\u662F\u6062\u590D\u4E3A cwnd \u521D\u59CB\u5316\u503C\uFF0C\u5047\u5B9A cwnd \u521D\u59CB\u5316\u503C 1</p><p>\u63A5\u7740\uFF0C\u5C31\u91CD\u65B0\u5F00\u59CB\u6162\u542F\u52A8\uFF0C\u6162\u542F\u52A8\u662F\u4F1A\u7A81\u7136\u51CF\u5C11\u6570\u636E\u6D41\u7684\u3002\u4E00\u65E6\u300C\u8D85\u65F6\u91CD\u4F20\u300D\uFF0C\u9A6C\u4E0A\u56DE\u5230\u89E3\u653E\u524D\u3002\u4F46\u662F\u8FD9\u79CD\u65B9\u5F0F\u592A\u6FC0\u8FDB\u4E86\uFF0C\u53CD\u5E94\u4E5F\u5F88\u5F3A\u70C8\uFF0C\u4F1A\u9020\u6210\u7F51\u7EDC\u5361\u987F</p><blockquote><p>\u53D1\u751F\u5FEB\u901F\u91CD\u4F20\u7684\u62E5\u585E\u53D1\u751F\u7B97\u6CD5</p></blockquote><p>\u5FEB\u901F\u91CD\u4F20\u7B97\u6CD5: \u5F53\u63A5\u6536\u65B9\u53D1\u73B0\u4E22\u4E86\u4E00\u4E2A\u4E2D\u95F4\u5305\u7684\u65F6\u5019\uFF0C\u53D1\u9001\u4E09\u6B21\u524D\u4E00\u4E2A\u5305\u7684 ACK\uFF0C\u4E8E\u662F\u53D1\u9001\u7AEF\u5C31\u4F1A\u5FEB\u901F\u5730\u91CD\u4F20\uFF0C\u4E0D\u5FC5\u7B49\u5F85\u8D85\u65F6\u518D\u91CD\u4F20\u3002</p><p>TCP \u8BA4\u4E3A\u8FD9\u79CD\u60C5\u51B5\u4E0D\u4E25\u91CD\uFF0C\u56E0\u4E3A\u5927\u90E8\u5206\u6CA1\u4E22\uFF0C\u53EA\u4E22\u4E86\u4E00\u5C0F\u90E8\u5206\uFF0C\u5219 ssthresh \u548C cwnd \u53D8\u5316\u5982\u4E0B\uFF1A</p><p>cwnd = cwnd/2 \uFF0C\u4E5F\u5C31\u662F\u8BBE\u7F6E\u4E3A\u539F\u6765\u7684\u4E00\u534A;<br> ssthresh = cwnd;</p><p>\u8FDB\u5165\u5FEB\u901F\u6062\u590D\u7B97\u6CD5</p><p><img src="'+t+'" alt="congestion2" loading="lazy"></p><h3 id="\u5FEB\u901F\u6062\u590D" tabindex="-1"><a class="header-anchor" href="#\u5FEB\u901F\u6062\u590D" aria-hidden="true">#</a> \u5FEB\u901F\u6062\u590D</h3><p>\u5FEB\u901F\u91CD\u4F20\u548C\u5FEB\u901F\u6062\u590D\u7B97\u6CD5\u4E00\u822C\u540C\u65F6\u4F7F\u7528</p><p>\u8FDB\u5165\u5FEB\u901F\u6062\u590D\u7B97\u6CD5\u5982\u4E0B\uFF1A</p><p>\u62E5\u585E\u7A97\u53E3 cwnd = ssthresh + 3 \uFF08 3 \u7684\u610F\u601D\u662F\u786E\u8BA4\u6709 3 \u4E2A\u6570\u636E\u5305\u88AB\u6536\u5230\u4E86\uFF09\uFF1B<br> \u91CD\u4F20\u4E22\u5931\u7684\u6570\u636E\u5305\uFF1B<br> \u5982\u679C\u518D\u6536\u5230\u91CD\u590D\u7684 ACK\uFF0C\u90A3\u4E48 cwnd \u589E\u52A0 1\uFF1B<br> \u5982\u679C\u6536\u5230\u65B0\u6570\u636E\u7684 ACK \u540E\uFF0C\u628A cwnd \u8BBE\u7F6E\u4E3A\u7B2C\u4E00\u6B65\u4E2D\u7684 ssthresh \u7684\u503C\uFF0C\u539F\u56E0\u662F\u8BE5 ACK \u786E\u8BA4\u4E86\u65B0\u7684\u6570\u636E\uFF0C\u8BF4\u660E\u4ECE duplicated ACK \u65F6\u7684\u6570\u636E\u90FD\u5DF2\u6536\u5230\uFF0C\u8BE5\u6062\u590D\u8FC7\u7A0B\u5DF2\u7ECF\u7ED3\u675F\uFF0C\u53EF\u4EE5\u56DE\u5230\u6062\u590D\u4E4B\u524D\u7684\u72B6\u6001\u4E86\uFF0C\u4E5F\u5373\u518D\u6B21\u8FDB\u5165\u62E5\u585E\u907F\u514D\u72B6\u6001</p><p><img src="'+r+'" alt="congestion3" loading="lazy"></p>',39),i=[o];function h(p,l){return s(),n("div",null,i)}var b=e(d,[["render",h],["__file","congestion-control.html.vue"]]);export{b as default};
